package org.ip.sesion03;

public class TrianguloRectanguloAsteriscos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7;
		  for(int j=n; j>0; j--) {  
		      for(int i=0; i<j; i++) {  
		        System.out.print( "* " );  
		      }  
		      System.out.println();  
		    }  
	}
}
